import React, { useEffect, useState } from 'react';
import './App.css';
import "bootstrap/dist/css/bootstrap.min.css";
import News from "./components/news.components";
import Form from "./components/form.component";

const App = () => {

  const API_KEY = "bf95d82ffbc54543870e6c3d38e0c295";

  // setResult = async(e) => {
  //   const newsfeed=e.target.elements.News.value
  //   const jokes=e.target.elements.jokes.value
  //   if( newsfeed ) {

  //   }
  //   else if( jokes ) {

  //   }
  //   else{
  //     this.setState({error: true});
  //   }
  // }

  const [news, setNews] = useState([]);
  const [search, setSearch] = useState("");
  // const [query, setQuery]=useState('ae');

  useEffect(() => {
    getNews();
  }, [search]);
// https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=bf95d82ffbc54543870e6c3d38e0c295
  const getNews = async(e) => {
    const api_call = await fetch(`https://newsapi.org/v2/top-headlines?country=${search}&apiKey=${API_KEY}`);
    const response= await api_call.json();
    setNews(response.articles);
    console.log(response);

    }

  const updateSearch = e => {
    setSearch(e.target.value);
    console.log(search);
  }

  // const getSearch = e => {
  //   e.preventdefault();
  //   setQuery(search);
  // }

  return (
      <div className="App">
        <h1>Apis</h1>
        <form>
        <label>
          Pick your favorite flavor:
          <select value={search} onChange={updateSearch}>
          <option value="ae">Arab Emirates</option>
            <option value="ar">Argentina</option>
            <option value="at">Austria</option>
            <option value="au">Australia</option>
            <option value="be">Belgium</option>
            <option value="bg">Bulgaria</option>
            <option value="br">Brazil</option>
            <option value="ca">Canada</option>
            <option value="ch">Switzerland</option>
            <option value="cn">China</option>
            <option value="co">Colombia</option>
            <option value="cu">Cuba</option>
            <option value="cz">Czechia</option>
            <option value="de">Germany</option>
            <option value="eg">Egypt</option>
            <option value="fr">France</option>
            <option value="gb">United Kingdom</option>
            <option value="gr">Greece</option>
            <option value="hk">Hong Kong</option>
            <option value="hu">Hungary</option>
            <option value="id">Indonesia</option>
            <option value="ie">Ireland</option>
            <option value="il">Israel</option>
            <option value="in">India</option>
            <option value="it">Italy</option>
            <option value="jp">Japan</option>
            <option value="kr">Korea</option>
            <option value="lt">Lithuania</option>
            <option value="lv">Latvia</option>
            <option value="ma">Morocco</option>
            <option value="mx">Mexico</option>
            <option value="my">Malaysia</option>
            <option value="ng">Nigeria</option>
            <option value="nl">Netherlands</option>
            <option value="no">Norway</option>
            <option value="nz">Newzealand</option>
            <option value="ph">Philippines</option>
            <option value="pl">Poland</option>
            <option value="pt">Portugal</option>
            <option value="ro">Romania</option>
            <option value="rs">Serbia</option>
            <option value="ru">Russia</option>
            <option value="sa">Saudi Arabia</option>
            <option value="se">Sweden</option>
            <option value="sg">Singapore</option>
            <option value="si">Slovenia</option>
            <option value="sk">Slovakia</option>
            <option value="th">Thailand</option>
            <option value="tr">Turkey</option>
            <option value="tw">Taiwan</option>
            <option value="ua">Ukraine</option>
            <option value="us">USA</option>
            <option value="ve">Venezuela</option>
            <option value="za">South Africa</option>
          </select>
        </label>
        {/* <button type="submit">search</button> */}
      </form>
        {news.map(source =>  (
          <News 
          key={source.publishedAt}
          title={source.title}
          image={source.urlToImage}
          description={source.description}
          url={source.url}
          content={source.content}
          author={source.author}
          time={source.publishedAt}
          />
        ))}
      </div>
    );


};
export default App;
