import React from "react";
import "./news.css";
import "bootstrap/dist/css/bootstrap.min.css";
const News = ({title,image,description}) => {
    return(
        <div className="polaroid">
            <div className="container">
                <h3>{title}</h3>
            </div>
                <img src={image} alt="" />
            <div className="container">
                <p>{description}</p>
            </div>
        </div>
    );
}

export default News;
