import React from "react";
import "./news.css";
import "bootstrap/dist/css/bootstrap.min.css";
const News = ({title,image,description,url,content,author}) => {
    return(
        <div className="polaroid">
            <div className="container">
                <h3><b>{title}</b></h3>
                <h5><b>{author}</b></h5>
            </div>
                <img src={image} alt="" />
            <div className="container">
                <p><b>{description}</b></p>
                <p>{content}</p>
                <a href={url} class="button button2"> more..</a>
            </div>
        </div>
    );
}

export default News;
