import React, { useEffect, useState } from 'react';
import './App.css';
import "bootstrap/dist/css/bootstrap.min.css";
import News from "./components/news.components";
import Form from "./components/form.component";

const App = () => {

  const API_KEY = "bf95d82ffbc54543870e6c3d38e0c295";

  // setResult = async(e) => {
  //   const newsfeed=e.target.elements.News.value
  //   const jokes=e.target.elements.jokes.value
  //   if( newsfeed ) {

  //   }
  //   else if( jokes ) {

  //   }
  //   else{
  //     this.setState({error: true});
  //   }
  // }

  const [news, setNews] = useState([]);
  const [search, setSearch] = useState("");
  const [query, setQuery]=useState('in');

  useEffect(() => {
    getNews();
  }, [query]);
// https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=bf95d82ffbc54543870e6c3d38e0c295
  const getNews = async(e) => {
    const api_call = await fetch(`https://newsapi.org/v2/top-headlines?country=${query}&apiKey=${API_KEY}`);
    const response= await api_call.json();
    setNews(response.articles);
    console.log(response);

    }

  const updateSearch = e => {
    setSearch(e.target.value);
  }

  const getSearch = e => {
    e.preventdefault();
    setQuery(search);
  }

  return (
      <div className="App">
        <h1>Apis</h1>
        <form onSubmit={getSearch}>
        <label>
          Pick your favorite flavor:
          <select value={search} onChange={updateSearch}>
            <option value="grapefruit">Grapefruit</option>
            <option value="lime">Lime</option>
            <option value="coconut">Coconut</option>
            <option value="mango">Mango</option>
          </select>
        </label>
        <input type="submit" value="Submit" />
      </form>
        {news.map(source =>  (
          <News 
          key={source.publishedAt}
          title={source.title}
          image={source.urlToImage}
          description={source.description}
          url={source.url}
          content={source.content}
          author={source.author}
          time={source.publishedAt}
          />
        ))}
      </div>
    );


};
export default App;
